<?php$digitalworld_blog_placehold  = digitalworld_option('digitalworld_blog_placehold','no');if( have_posts()){	?>	<div class="post-list post-items">        <?php        while( have_posts()){        the_post();        ?>        <div <?php post_class('post-item');?>>            <?php if( has_post_thumbnail() || $digitalworld_blog_placehold =='yes' ): ?>                <div class="post-thumb">                    <?php digitalworld_post_thumbnail( );?>                </div>            <?php endif;?>            <div class="post-item-info">                <h3 class="post-name">                    <a href="<?php the_permalink();?>"><?php the_title();?></a>                </h3>                <div class="post-metas">                    <?php                    if ( is_sticky() && is_home() && ! is_paged() ) {                        printf( '<span class="sticky-post">%s</span>', esc_html__( 'Sticky', 'digitalworld' ) );                    }                    ?>                    <span class="author"><?php esc_html_e('Posted by:','digitalworld');?> <?php the_author();?></span>                    <span class="time">                        <?php echo get_the_date('M d, Y');?>                    </span>                </div>                <div class="post-excerpt"><?php echo wp_trim_words(apply_filters('the_excerpt', get_the_excerpt()), 40, __('...', 'digitalworld')); ?></div>                <a href="<?php the_permalink();?>" class="button"><span class="text"><?php esc_html_e('Read more','digitalworld');?></span> <span class="icon flaticon-arrows-3"></span></a>            </div>        </div>        <?php    }    ?>    </div>	<?php	digitalworld_paging_nav();}else{	get_template_part( 'content', 'none' );}